using namespace std;
typedef long long LL;
struct P
{
	double x,y;
	P(){}
	P(double a,double b)
	{
		x = a;
		y = b;
	}
};
int n,q;
vector<P> poly;

double dis(P a,P b)
{
	return sqrt( (a.x-b.x)*(a.x-b.x) + (a.y-b.y)*(a.y-b.y) );
}

bool onPolygon(vector<P> &polygon, P p)
{
	int cnt = polygon.size();
	P cur,nxt;
	double t;

	REP(i,0,cnt-1)
	{
		cur = polygon[i];
		nxt = polygon[(i+1)%cnt];

		t = dis(p,cur) + dis(p,nxt) - dis(cur,nxt);
		if( fabs(t) <= 1e-9 ) return true;
	}
	return false;
}

bool windingNumber(vector<P> &polygon, P p)
{
	int w=0;
	int cnt = polygon.size();
	P cur,nxt;

	REP(i,0,cnt-1)
	{
		cur = polygon[i];
		nxt = polygon[(i+1)%cnt];

		if( (cur.x>=p.x) ^ (nxt.x>=p.x) )
			w += (cur.x-p.x) * (nxt.y-p.y) >= (nxt.x-p.x) * (cur.y-p.y) ? 1 : -1;
	}
	return w!=0;
}

int main()
{
	P p;
	int x,y;
	RI(n);
	REP(i,1,n)
	{
		RI(x,y);
		poly.PB( P(x,y) );
	}

	RI(q);
	while(q--)
	{
		RI(x,y);
		p = P(x,y);

		if( onPolygon(poly, p) )
			puts("1");
		else if( windingNumber(poly, p) )
			puts("2");
		else
			puts("0");
	}
	return 0;
}

