  Point () {}
  Point ( ll x = 0, ll y = 0 ) : x(x), y(y) {}

  Point operator - ( const Point &p ) const { return Point(x - p.x, y - p.y); }
};

typedef Point Vector;

typedef vector<Point> Polygon;

ll dot( Vector a, Vector b ) { return a.x*b.x + a.y*b.y; }
ll cross( Vector a, Vector b ) { return a.x*b.y - a.y*b.x; }

enum { IN, ONSEG, OUT };
#define curr(G, i) (G[i])
#define next(G, i) (G[(i+1)%n])

int
pointInPolygon(const Polygon &G, const Point &p) {

  int n = G.size();
  bool in = false;

  for(int i=0; i<n; i++) {
    Vector V1 = curr(G, i) - p, V2 = next(G, i) - p;

    if( V1.y > V2.y ) swap(V1, V2);
    if( V1.y <= 0 && 0 < V2.y && cross(V1, V2) > 0 ) in = !in;
    if( cross(V1, V2) == 0 && dot(V1, V2) <= 0 ) return ONSEG;
  }
  return in ? IN : OUT;
}

