		cin >> p.x >> p.y;
		poly.push_back(p);
	}
	cin >> q;
	Point p;
	while (q--) {
		cin >> p.x >> p.y;
		cout << contains(poly, p) << endl;
	}
}

#include <bits/stdc++.h>
using namespace std;
using ll = long long;
using u64 = uint_fast64_t; 
