double atan(Point p) { return atan2(p.y, p.x); }
double norm(Point p) { return p.x * p.x + p.y * p.y; }
double abs(Point p) { return sqrt(norm(p)); }
double distancePP(Point p, Point o) { return sqrt(norm(o - p)); }

int ccw(Point a, Point b, Point c) {
	b = b-a;
	c = c-a;

	if(cross(b, c) > 0.0) return +1;	//conter clockwise
	if(cross(b, c) < 0.0) return -1;	//clockwise
	if(dot(b, c) < 0.0) return +2;	//a on Seg(b,c)
	if(norm(b) < norm(c)) return -2;	//b on Seg(a,c)
	return 0;	//c on Seg(a,b)
}

double polygonArea(const vector<Point>& p) {
	int n = p.size();
	double ret = 0.0;
	for(int i = 0; i < n; ++i) {
		ret += cross(p[i], p[(i+1)%n]);
	}

	return abs(ret) / 2.0;
}

bool isConvex(const vector<Point>& p) {
	int n = p.size();
	rep(i, n) {
		if(ccw(p[i], p[(i+1)%n], p[(i+2)%n]) == -1) return false;
	}
	return true;
}

// IN : 2, ON : 1, OUT : 0
int contains(const vector<Point>& v, Point p) {
	int n = v.size();
	bool flag = false;
	rep(i, n) {
		Point a = v[i] - p, b = v[(i+1)%n] - p;
		if(a.y > b.y) swap(a, b);
		if(a.y <= 0.0 && 0 < b.y && cross(a, b) > 0.0) flag = !flag;
		if(cross(a, b) == 0 && dot(a, b) <= 0) return 1;
	}
	return flag ? 2 : 0;
}

