	}

	struct Line{
		db k,b;
		Line(){ ; } 
		Line(db x,db y){ k=x,b=y; }
		Line(Point x,Point y) {
			assert(cmp(x.x,y.x) || cmp(x.y,y.y));
			if(cmp(x.x,y.x)==0) k=inf,b=x.x;
			else {
				db t=(y.y-x.y)/(y.x-x.x);
				k=t,b=x.y-x.x*k;
			}
		}
		db operator [] (const db x){ return k==inf? Nan : k*x+b; }
	};
	int operator == (Line a,Line b){
		if(a.k==inf && b.k==inf) return cmp(a.b,b.b)==0;
		if(a.k==inf || b.k==inf) return 0;
		return cmp(a.b,b.b)==0 && cmp(a.k,b.k)==0;
	}
	db operator & (Line x,Line y){ 
		assert(fabs(x.k-y.k)>eps);
		if(y.k==inf) swap(x,y);
		if(x.k==inf) return x.b;
		return (x.b-y.b)/(y.k-x.k); 
	}
	db Dis(Line x,Point y){ 
		if(x.k==inf) return abs(x.b-y.x);
		return fabs((x[y.x]+y.y)/sqrt(x.k*x.k+1)); 
	}
	int Parallel(Line x,Line y){ 
		if(x.k==inf && y.k==inf) return 1;
		if(x.k==inf || y.k==inf) return 0;
		return cmp(x.k,y.k)==0;
	}

