enum { OUT, ON, IN };
int contains(const polygon &P, const point &p)
{
	bool in = false;
	for (int i = 0, n = P.size(); i < n; ++i)
	{
		point a = P[i] - p, b = P[(i + 1) % n] - p;
		if (imag(a) > imag(b)) swap(a, b);
