    p[i] = P(x,y);
  }

  int q;
  scanf("%d",&q);

  for(int i=0;i<q;i++){
    int x,y;
    scanf("%d%d",&x,&y);

    printf("%d\n", iscontainedPolyP(n, p, P(x,y)));
  }


  return 0;
}

