			if(dcmp(cross(p1-p,p2-p))==0 && dcmp(dot(p1-p,p2-p))<=0)return 1;
			Point x=line_intersection(Line(p,pp),Line(p1,p2));
			if(x.x==233 && x.y==233)continue;
