class Point{
public:
	Point(const int &xa, const int &ya):x(xa),y(ya){};
	Point() = default;
	int x, y;
};

class Line{
public:
	Line(const Point &&ap1, const Point &&ap2){
		if (ap1.y > ap2.y){
			p1 = ap1;
			p2 = ap2;
		}else{
			p1 = ap2;
			p2 = ap1;
		}
	}
	Point p1, p2;
	bool is_on_line(const Point &p){
		return (((p1.x - p2.x) * (p.y - p2.y) == (p1.y - p2.y) * (p.x - p2.x)) and ((p1.x - p.x) * (p2.x - p.x) <= 0) and ((p1.y - p.y) * (p2.y - p.y) <= 0));
	}
	int cross_line(const Point &p){
		if (is_on_line(p)){
			return 1000;//辺に接する
		}else if (p.y == p2.y){
			return 0;//下側の頂点を通る
		}else if ((p1.y - p.y) * (p2.y - p.y) <= 0 and ((p.y - p2.y) * (p1.x - p2.x) > (p1.y - p2. y) * (p.x - p2.x))){
			return 1;//上側の頂点か辺を通る
		}else{
			return 0;//交わらない
		}
	}
};
int fold(std::vector<Line> &lines, const Point &&p){
	int sum = 0;
	for (auto l : lines){
		sum += l.cross_line(p);
	}
	if (sum >= 1000){
		return 1;
	}else if (sum % 2 == 1){
		return 2;
	}else{
		return 0;
	}
}

