inline Vector pro(Vector base, Vector v, Vector be) {
	double a = dot(base, v) / squ(base);
	Vector v1 = base * a;
	return v1 + be;
}
inline Vector ref(Vector v, Vector be) {
	return (v - be)*2 + be;
}
inline double cross(Vector v1, Vector v2) {
	return v1.x*v2.y - v1.y*v2.x;
}
inline int ccw(Vector v1, Vector v2, Vector v3) {
	Vector a = v2 - v1;
	Vector b = v3 - v1;
	if (cross(a, b) < 0.0) return -1;
	else if (cross(a, b) > 0.0) return 1;
	else if (dot(a, b) < 0.0) return -2;
	else if (dot(a, b) > squ(a)) return 2;
	else return 0;
}
inline int inter(Vector v1, Vector v2, Vector v3, Vector v4) {
	return (ccw(v1, v2, v3) * ccw(v1, v2, v4) <= 0) && (ccw(v3, v4, v1) * ccw(v3, v4, v2) <= 0);
}
inline double disPP(Vector v1, Vector v2) {
	return norm(v2 - v1);
}
inline double disLP(Vector v1, Vector v2, Vector v3) {
	Vector a = v2 - v1;
	Vector b = v3 - v1;
	return fabs(cross(a, b) / norm(a));
}
inline double disSP(Vector v1, Vector v2, Vector v3) {
	Vector a = v2 - v1;
	Vector b = v3 - v1;
	Vector c = v1 - v2;
	Vector d = v3 - v2;
	if (dot(a, b) < 0.0) return disPP(v1, v3);
	else if (dot(c, d) < 0.0) return disPP(v2, v3);
	else return disLP(v1, v2, v3);
}
inline double disSS(Vector v1, Vector v2, Vector v3, Vector v4) {
	if (inter(v1, v2, v3, v4)) return 0;
	double a = disSP(v1, v2, v3);
	double b = disSP(v1, v2, v4);
	double c = disSP(v3, v4, v1);
	double d = disSP(v3, v4, v2);
	return min(min(a, b), min(c, d));
}
inline Vector getcross(Vector v1, Vector v2, Vector v3, Vector v4) {
	Vector v5 = pro(v2 - v1, v3 - v1, v1);
	Vector v6 = pro(v2 - v1, v4 - v1, v1);
	double a = disPP(v3, v5);
	double b = disPP(v4, v6);
	Vector vans = (v6 - v5)*a*(1 / (a + b)) + v5;
	return vans;

}
inline pair<Vector, Vector> getccl(double r, Vector o, Vector v1, Vector v2) {
	Vector ev = (v2 - v1)*(1 / norm(v2 - v1));
	Vector pr = pro(v2 - v1, o - v1, v1);
	double l = sqrt(r *r - squ(o - pr));
	Vector ans1 = ev * l + pr;
	Vector ans2 = pr - ev * l;
	if (ans1.x > ans2.x) swap(ans1, ans2);
	else if (ans1.x == ans2.x)
		if (ans1.y > ans2.y) swap(ans1, ans2);
	return make_pair(ans1, ans2);
}
inline pair<point, point> getccc(circle c1, circle c2) {
	Vector v1(c2.p.x - c1.p.x, c2.p.y - c1.p.y);
	double dis = norm(v1);
	double a = acos((dis*dis + c1.r*c1.r - c2.r*c2.r) / (2 * c1.r*dis));
	double b = atan2(c2.p.y - c1.p.y, c2.p.x - c1.p.x);
	point pa(c1.r*cos(a + b), c1.r*sin(a + b));
	point pb(c1.r*cos(b - a), c1.r*sin(b -a));
	point ans1(c1.p + pa);
	point ans2(c1.p + pb);
	if (ans1.x > ans2.x || ((ans1.x == ans2.x) && (ans1.y > ans2.y))) swap(ans1, ans2);
	return make_pair(ans1, ans2);
}
