using namespace std;

#define EPS 1e-9
#define PI acos(-1.0)

struct point {
	double x, y;
	point() { x = y = 0.0; }
	point(double x, double y) : x(x), y(y) {}
	bool operator < (point other) const {
		if (fabs(x - other.x) > EPS) return x < other.x;
		return y < other.y;
	}
	bool operator == (point other) const {
		return (fabs(x - other.x) < EPS && fabs(y - other.y) < EPS);
	}
    point operator - (point other) const {
        return point(x - other.x, y - other.y);
    }
};

int g,q;
vector<point> cs;

double cross(point a, point b) {
	return a.x*b.y - a.y*b.x;
}

bool ccw(point p, point q, point r) {
    return cross(q-p, r-p) > 0;
}

double sqrdist(point p1, point p2) {  
    return (p1.x - p2.x)*(p1.x - p2.x) + (p1.y - p2.y)*(p1.y - p2.y);  
}

double getAngle(point a, point b, point c) {
    double A = sqrdist(a,c);
    double B = sqrdist(a,b);
    double C = sqrdist(b,c);
    return acos((B+C-A)/(2*sqrt(B)*sqrt(C)));
}

int sumAngles(point p) {
    double angle = 0;
    for (int i = 0; i < cs.size() - 1; i++) {
        double newAngle = getAngle(cs[i], p, cs[i+1]);
        if (fabs(PI-fabs(newAngle)) < EPS) return 1; 

        if (ccw(p, cs[i], cs[i+1])) {
            angle += newAngle;
        } else {
            angle -= newAngle;
        }
    }
    if (fabs(2*PI-fabs(angle)) < EPS) {
        return 2;
    } else {
        return 0;
    }
}

int main() {
    cin >> g;
    for (int i = 0; i < g; i++) {
        int a,b; cin >> a >> b;
        cs.push_back(point(a,b));
    }
    cs.push_back(cs[0]);

    cin >> q;
    for (int i = 0; i < q; i++) {
        int a,b; cin >> a >> b;
        cout << sumAngles(point(a,b)) << endl;
    }
    return 0;
}

