typedef complex<double> P;
const P pINF(sqrt(2) * INF, sqrt(235) * INF);

double cross(const P &a, const P &b){
    return imag(conj(a)*b);
}

double dot(const P &a, const P &b){
    return real(conj(a)*b);
}

int ccw(P a, P b, P c){
    b -= a;
    c -= a;
    if(cross(b,c) > 0) return +1;
    if(cross(b,c) < 0) return -1;
    if(dot(b,c) < 0) return +2;
    if(norm(b) < norm(c)) return -2;
    return 0;
}

struct L : public vector<P> {
    L(const P &a, const P &b) {
        push_back(a); push_back(b);
    }
};

bool intersectLL(const L &l, const L &m){
    return abs(cross(l[1]-l[0], m[1]-m[0])) > EPS || abs(cross(l[1]-m[0], m[1]-m[0])) < EPS;
}

bool intersectLS(const L &l, const L &s) {
    return cross(l[1]-l[0], s[0]-l[0]) * cross(l[1]-l[0], s[1]-l[0]) < EPS;
}
bool intersectLP(const L &l, const P &p) {
    return abs(cross(l[1]-p, l[0]-p)) < EPS;
}
bool intersectSS(const L &s, const L &t) {
    return ccw(s[0],s[1],t[0])*ccw(s[0],s[1],t[1]) <= 0 && ccw(t[0],t[1],s[0])*ccw(t[0],t[1],s[1]) <= 0;
}
bool intersectSP(const L &s, const P &p) {
    return abs(s[0]-p)+abs(s[1]-p)-abs(s[1]-s[0]) < EPS; // triangle inequality
}

int polygonPointContainment(vector<P> &G, P p){
    int inside = 0;
    for (int i=0;i<G.size()-1;i++){
        P q = G[i] - p;
        P r = G[i+1] - p;
        if(imag(q) > imag(r)) swap(q,r);
            if(imag(q) <= 0 && imag(r) > 0 && cross(q,r) < 0){
                inside = 1 - inside;
            }
        if(cross(q,r) == 0 && dot(q,r) <= 0){
          return 1;
        }
    }
    return 2*inside;
}

