int in_polygon(const VP& pol, const P& p){
	int n=pol.size(),res=0;
	rep(i,n){
		if(ccw(pol[i],pol[(i+1)%n],p)==ON) return 1;
		P cur=p-pol[i],nxt=p-pol[(i+1)%n];
		bool f=(sgn(imag(cur))>=0);
		bool s=(sgn(imag(nxt))<0);
		bool can=(sgn(imag(cur*nxt))>0);
		if(can&&f==s) f?res++:res--;
	}
	return res?2:0;
}


