const double EPS = 1e-9;


struct Edge {
    int x0, y0, x1, y1;
    Edge() {}
    void set(int a, int b, int c, int d) {
        if (a > c) {
            swap(a, c);
            swap(b, d);
        }
        x0 = a; y0 = b;
        x1 = c; y1 = d;
    }
    bool operator<(const Edge& e) const {
        return x0 < e.x0;
    }
};

bool dequals(double a, double b) {
    return abs(b - a) < EPS;
}

int main() {
    int N, Q;
    cin >> N;
    int x0, y0, px, py, x, y;
    vector<Edge> edge(N);

    cin >> x0 >> y0;
    px = x0; py = y0;
    FOR(i, 1, N) {
        cin >> x >> y;
        edge[i-1].set(px, py, x, y);
        px = x; py = y;
    }
    edge[N-1].set(px, py, x0, y0);
    sort(begin(edge), end(edge));

    cin >> Q;
    FOR(i, 0, Q) {
        cin >> x >> y;
        int ecount = 0;
        int result = 0;
        FOR(i, 0, N) {
            Edge &e = edge[i];
            if (x < e.x0) {
                break;
            }
            double p = (double)(y - e.y0) / (e.y1 - e.y0);
            if (p < 0 || p > 1.0) continue;
            double cx = e.x0 + p * (e.x1 - e.x0);
            if (dequals((double)x, cx)) {
                result = 1;
                break;
            }
            ecount++;
        }
        if (result != 1 and ecount % 2 == 1) result = 2;
        cout << result << endl;
    }

    return 0;
}

