	inline bool check(polygon v){
		int n=siz(v),f=(cross(v[n-1]-v[n-2],v[0]-v[n-1])>=-eps);
		For(i,1,n-2) if ((cross(v[i]-v[i-1],v[i+1]-v[i])>=-eps)!=f) return 0;
		return 1;
	}
	inline int check(point a,polygon v){
		db k=sqrt(23);point b=(point){a.x+100000000,a.y+k*100000000};
