int contains(Polygon g, Point p) {
  int n = g.size();
  bool x = false;
  for (int i = 0; i < n; ++i) {
    Point a = g[i] - p, b = g[(i + 1) % n] - p;
    if (abs(cross(a, b)) < EPS && dot(a, b) < EPS) return 1;
    if (a.y > b.y) swap(a, b);
    if (a.y < EPS & EPS < b.y && cross(a, b) > EPS) x = !x;
  }
  return (x ? 2 : 0);
}

Polygon convexHull(Polygon s) {
  Polygon u, l;
  if (s.size() < 3) return s;
  sort(s.begin(), s.end());
  u.push_back(s[0]);
  u.push_back(s[1]);
  l.push_back(s[s.size() - 1]);
  l.push_back(s[s.size() - 2]);

  for (int i = 2; i < s.size(); ++i) {
    for (int n = u.size(); n >= 2 && ccw(u[n - 2], u[n - 1], s[i]) != CLOCKWISE;
         --n) {
      u.pop_back();
    }
    u.push_back(s[i]);
  }

  for (int i = s.size() - 3; i >= 0; i--) {
    for (int n = l.size(); n >= 2 && ccw(l[n - 2], l[n - 1], s[i]) != CLOCKWISE;
         --n) {
      l.pop_back();
    }
    l.push_back(s[i]);
  }

  reverse(l.begin(), l.end());
  for (int i = u.size() - 2; i >= 1; --i) l.push_back(u[i]);

  return l;
}

Circle circumscribedCircle(const Point &a, const Point &b, const Point &c) {
  double a1, a2, b1, b2, c1, c2;
  a1 = 2 * (b.x - a.x);
  b1 = 2 * (b.y - a.y);
  c1 = a.x * a.x - b.x * b.x + a.y * a.y - b.y * b.y;
  a2 = 2 * (c.x - a.x);
  b2 = 2 * (c.y - a.y);
  c2 = a.x * a.x - c.x * c.x + a.y * a.y - c.y * c.y;
  Point p;
  p.x = (b1 * c2 - b2 * c1) / (a1 * b2 - a2 * b1);
  p.y = (c1 * a2 - c2 * a1) / (a1 * b2 - a2 * b1);

  return Circle(p, abs(p - a));
}

// https://en.wikipedia.org/wiki/Green's_theorem#Area_Calculation
double area(const Polygon &p) {
  double ans = 0;
  const int N = p.size();
  for (int i = 0; i < N; ++i) {
    Point q = p[(i + 1) % N];
    ans += (p[i].y + q.y) * (q.x - p[i].x);
  }
  ans /= 2.0;
  return fabs(ans);
}

// The points must be counter-clockwise order
bool isConvex(const Polygon &p) {
  const int N = p.size();

  for (int i = 0; i < N; ++i) {
    Point pn = p[(i + 1) % N], pnn = p[(i + 2) % N];
    int d = ccw(p[i], pn, pnn);
    if (d == CLOCKWISE) return false;
  }
  return true;
}

