const double eps = 1e-10;
typedef complex<double> Point;

inline double cross(const Point& a, const Point& b){
    return imag(conj(a) * b);
}

typedef vector<Point> Polygon;

int convexcontains(const Polygon& ch, const Point& p){
    Point g = (ch[0] + ch[(int)ch.size()/3] + ch[(int)ch.size()*2/3]) / 3.0;
    int l = 0, r = (int)ch.size();
    while(l + 1 < r){
        int m = (l + r) >> 1;
        if(cross(ch[l] - g, ch[m] - g) > 0){
            (cross(ch[l] - g, p - g) > 0 && cross(ch[m] - g, p - g) < 0? r: l) = m;
        }
        else{
            (cross(ch[l] - g, p - g) < 0 && cross(ch[m] - g, p - g) > 0? l: r) = m;
        }
    }
    r %= (int)ch.size();
    if(cross(ch[l] - p, ch[r] - p) > eps)return 2;
    if(cross(ch[l] - p, ch[r] - p) < -eps)return 0;
    return 1;
}

