using namespace std;

struct P {
    int x, y;

    P() : x(0), y(0) { }
    P(int x, int y) : x(x), y(y) { }

    int norm() { return x * x + y * y; }
    int dot(P p) { return x * p.x + y * p.y; }
    int cross(P p) { return x * p.y - y * p.x; }

    P operator -() { return P(-x, -y); }
    P operator +(P p) { return P(x + p.x, y + p.y); }
    P operator -(P p) { return P(x - p.x, y - p.y); }
    P operator *(int k) { return P(x * k, y * k); }
};

int ccw(P p0, P p1, P p2) {
    P u = p1 - p0;
    P v = p2 - p0; 

    if (u.cross(v) > 0) return 1;
    if (u.cross(v) < 0) return -1;
    if (u.dot(v) < 0) return 2;
    if (u.norm() < v.norm()) return -2;

    return 0;
}

const int MAX_N = 100;

int n;
P ps[MAX_N + 2];

int solve(int x, int y) {
    int c = 0;

    P d1(x, y);
    P d2(10001, y);

    for (int i = 0; i < n; i++) {
        if (ccw(ps[i], ps[i + 1], d1) == 0) return 1;

        if (ccw(d1, d2, ps[i]) * ccw(d1, d2, ps[i + 1]) < 0 &&
            ccw(ps[i], ps[i + 1], d1) * ccw(ps[i], ps[i + 1], d2) < 0) c++;

        if (ccw(d1, d2, ps[i + 1]) == 0 && ccw(d1, d2, ps[i]) * ccw(d1, d2, ps[i + 2]) < 0) c++;
    }

    return c % 2 ? 2 : 0;
}

int main() {
    cin >> n;
    for (int i = 0; i < n; i++) {
        int x, y;
        cin >> x >> y;

        ps[i] = P(x, y);
    }
    ps[n] = ps[0];
    ps[n + 1] = ps[1];

    int q;
    cin >> q;

    for (int i = 0; i < q; i++) {
        int x, y;
        cin >> x >> y;
        cout << solve(x, y) << endl;
    }
}

