const double eps = 1e-10;
bool dcmp(double d, double d2 = 0) { return abs(d - d2) < eps; }

struct vec {
  double x, y;
  vec(double x, double y) : x(x), y(y) {}
  vec() : x(0), y(0) {}
  const bool operator==(const vec &v) const {
    return dcmp(x, v.x) && dcmp(y, v.y);
  }
  const bool operator!=(const vec &v) const { return !(*this == v); }
  const vec operator+(const vec &v) const { return vec(x + v.x, y + v.y); }
  const vec operator-() const { return vec(-x, -y); }
  const vec operator-(const vec &v) const { return *this + (-v); }
  const vec operator*(const double d) const { return vec(x * d, y * d); }
  const vec operator/(const double d) const { return *this * (1 / d); }
  const vec unit() const { return *this / len(); };
  const double len() const { return sqrt(x * x + y * y); }
  const double dot(const vec &v) const { return x * v.x + y * v.y; }
  const double cross(const vec &v) const { return x * v.y - y * v.x; }
  const bool parallel(const vec &v) const { return dcmp(cross(v)); }
  const bool perpendicular(const vec &v) const { return dcmp(dot(v)); }
  const bool clockwise(const vec &v) const { return cross(v) < 0; }
  const bool anticlockwise(const vec &v) const { return cross(v) > 0; }
};

typedef vec point;

struct polygon {
  // All points in ANTI-CLOCKWISE order
  vector<point> pts;
  const bool pointOnSide(const point &p) const {
    for (point g : pts)
      if (p == g)
        return true;
    for (int i = 0; i < pts.size(); i++) {
      vec s = pts[(i + 1) % pts.size()] - pts[i];
      vec v = p - pts[i];
      if (v.unit() == s.unit() && v.len() <= s.len()) {
        return true;
      }
    }
    return false;
  }
  const bool pointInPolygon(const point &p) const {
    if (pointOnSide(p))
      return false;
    int cw = 0, ccw = 0;
    for (int i = 0; i < pts.size(); i++) {
      vec s1 = pts[(i + 1) % pts.size()] - p;
      vec s2 = pts[i] - p;
      if (s1.anticlockwise(s2))
        ccw++;
      else if (s1.clockwise(s2))
        cw++;
    }
    if (!cw || !ccw)
      return true;
    return false;
  }
};

int main() {
  polygon g;
  {
    int n;
    cin >> n;
    for (int i = 0; i < n; i++) {
      point p;
      cin >> p.x >> p.y;
      g.pts.push_back(p);
    }
  }
  int q;
  cin >> q;
  for (int i = 0; i < q; i++) {
    point p;
    cin >> p.x >> p.y;
    if (g.pointOnSide(p)) {
      puts("1");
    } else if (g.pointInPolygon(p)) {
      puts("2");
    } else {
      puts("0");
    }
  }
}

