class Line
{
public:
    Point s,e;
    Line() {}
    Line(Point s,Point e):s(s),e(e) {}
};
bool onSge(Line l,Point q)
{
    return ((l.s-q)^(l.e-q))==0&&((l.s-q)*(l.e-q))<=0;
}
Point operator *(double d,Point p)
{
    return Point(p.x*d,p.y*d);
}
Point getProject(Point p1,Point p2,Point q) //q 在直线p1,p2上的投影坐标
{
    double d1=1.0/(p2-p1).len();
    Point ans=(q-p1)*(p2-p1)*(p2-p1)*d1*d1;
    return ans+p1;
}
Point getReflect(Point p1,Point p2,Point q) //q 关于直线p1 p2的 反射坐标
{
    return 2.0*getProject(p1,p2,q)-q;
}
double getDisPP(Point a,Point b) //两点之间距离
{
    return (b-a).len();
}
double getDisPL(Line l,Point p) //点到直线距离
{
    Point x=getProject(l.s,l.e,p);
    return (p-x).len();
}
double getDisPS(Line l,Point p) //点到线段最短距离
{
    Point x=getProject(l.s,l.e,p);
    if(onSge(l,x))
        return (x-p).len();
    else
        return min((p-l.s).len(),(p-l.e).len());

}
double getDisSS(Line la,Line lb) //保证两线段不相交 isInterSS==false
{
    return min(min(getDisPS(la,lb.s),getDisPS(la,lb.e)),
               min(getDisPS(lb,la.s),getDisPS(lb,la.e)));
}

int getDirPPP(Point p,Point p1,Point p2)
{
    return sgn((p1-p)^(p2-p));//1代表 p2在p1的逆时针方向  0共线 ，-1是p2在p1的顺时针方向
}
int getStatePP(Point p,Point p1,Point p2) //返回 p2 关于p 在p1的什么方向
