void sortPolarAngle(Point PA[],int n)//对数组进行极角排序  点将逆时针排序 WA
{
    Point p0=PA[0];
    int k=0;
    /* 找到最下边的一个点 靠左的一个点*/
    for(int i=1; i<n; ++i)
    {
        if((p0.y>PA[i].y)||(p0.y==PA[i].y&&p0.x>PA[i].x))
        {
            p0=PA[i];
            k=i;
        }
    }
    swap(PA[k],PA[0]);
    sort(PA+1,PA+n,_cmp);
    return ;
}
