int getStatePolygonP(Point p[],int n,Point q)
{
    for(int i = 0; i < n; i++)
    {
        if(p[i].x==q.x&&p[i].y==q.y)return 3;
    }

    for(int i = 0; i < n; i++)
    {
        int j=(i+1)%n;
        if(onSge(Line(p[j],p[i]),q))return 2;
    }
    int cnt = 0;
    for(int i = 0 ; i < n; i++)
    {
        int j=(i+1)%n;
        int k = sgn((q-p[j])^(p[i]-p[j]));
        int u = sgn(p[i].y-q.y);
        int v = sgn(p[j].y-q.y);
        if(k > 0 && u < 0 && v >= 0)    cnt++;
        if(k < 0 && v < 0 && u >= 0)    cnt--;
    }
    return cnt != 0;
}
