using namespace std;

static const double EPS=1e-12;
static const double INF=1e24;

using Point=complex<double>;
using Plane=vector<Point>;
using Polygon=vector<Point>;

bool operator<(const Point &a, const Point &b) {
    return real(a)!=real(b)? real(a)<real(b) : imag(a)<imag(b);
}

double cross_prod(const Point &a, const Point &b) {
    return imag(conj(a)*b);
}

double dot_prod(const Point &a, const Point &b) {
    return real(conj(a)*b);
}

enum {
    ONLINE_FRONT=-2,
    CLOCKWISE,
    ON_SEGMENT,
    COUNTER_CLOCKWISE,
    ONLINE_BACK,
};

int ccwise(Point a, Point b, Point c) {
    b -= a; c -= a;

    if (cross_prod(b, c) > 0) {
        return COUNTER_CLOCKWISE;
    } else if (cross_prod(b, c) < 0) {
        return CLOCKWISE;
    } else if (dot_prod(b, c) < 0) {
        return ONLINE_BACK;
    } else if (norm(b) < norm(c)) {
        return ONLINE_FRONT;
    } else {
        return ON_SEGMENT;
    }
}

enum {
    OUT_OF_LINE,
    ON_LINE,
    IN_LINE,
};

int contains(const Polygon &g, const Point &p) {
    bool in=false;
    size_t V=g.size();
    for (size_t i=0; i<V; ++i) {
        Point a=g[i]-p, b=g[(i+1)%V]-p;
        if (imag(a) > imag(b)) swap(a, b);
        if ((imag(a) <= 0) && (imag(b) > 0) && (cross_prod(a, b) < 0))
            in = !in;

        if (abs(cross_prod(a, b)) < EPS && dot_prod(a, b) <= EPS)
            return ON_LINE;
    }
    return in? IN_LINE : OUT_OF_LINE;
}

