class Vector
{
public:
  double x;
  double y;

  Vector(){}
  Vector( double x, double y ) : x(x), y(y) {}

  Vector operator + ( const Vector v ) { return Vector( x + v.x, y + v.y ); }
  Vector operator - ( const Vector v ) { return Vector( x - v.x, y - v.y ); }
  Vector operator * ( const double k ) { return Vector( x * k, y * k ); }
  Vector operator / ( const double k ) { return Vector( x / k, y / k ); }
  bool operator < ( const Vector &v ) const { return x * x + y * y < v.x * v.x + v.y * v.y; }

  double abs() { return sqrt( norm() ); }
  double norm() { return x * x + y * y; }

  static double dot( Vector v1, Vector v2 ) { return v1.x * v2.x + v1.y * v2.y; }
  static double cross( Vector v1, Vector v2 ) { return v1.x * v2.y - v1.y * v2.x; }

  static double arg( Vector v1, Vector v2 );
  void print() { cout << fixed << setprecision(10) << x << " " << y << endl; }
};

double Vector::arg( Vector v1, Vector v2 )
{
  Vector vd = v2 - v1;
  double a;

  if ( v1.norm() < eps || v2.norm() < eps )
    return pi;
  else
    a = acos( ( v1.norm() + v2.norm() - vd.norm() ) / ( 2 * v1.abs() * v2.abs() ) );

  if ( cross( v1, v2 ) > 0 )
    return a;
  else
    return -a;
}

class Polygon
{
public:
  int n;
  vector<Vector> pt;
  Polygon(){}
  Polygon( int n ) : n(n), pt(n) {}
  int solve( Vector p );
};

int Polygon::solve( Vector p )
{
  double sum = 0;
  Vector a, b;

  for ( auto i = 0; i < n; ++i )
    {
      if ( i )
        {
          a = pt[i-1] - p;
          b = pt[i] - p;
        }
      else
        {
          a = pt[n-1] - p;
          b = pt[i] - p;
        }

      double r = Vector::arg( a, b );
      if ( fabs( fabs( r ) - pi ) < eps )
        {
          cout << "1" << endl;
          return 0;
        }
      else
        sum += r;
    }

  if ( fabs( sum - 2 * pi ) < eps )
    cout << "2" << endl;
  else
    cout << "0" << endl;

  return 0;
}

