  rep (i, n) {
    Point a = ps[i] - p;
    Point b = ps[(i + 1) % n] - p;
    if (EQ(cross(a,b), 0) && LE(dot(a,b), 0)) return 1;
    if (a.Y > b.Y) swap(a,b);
    if ((a.Y*b.Y < 0 || (a.Y*b.Y < EPS && b.Y > EPS)) && LE(cross(a, b), 0)) in = !in;
  }
  return (in ? 2 : 0);
}

int main(void){
    int n,q;
    double x,y;
    cin>>n;
    VP g(n);
    rep(i,n){
        cin>>x>>y;
        g[i] = {x,y};
    }
    cin>>q;
    rep(i,q){
    	cin>>x>>y;
    	cout<<inPolygon(g,Point(x,y))<<endl;
    }
    return 0;
}

