typedef complex<double> P;
typedef vector<P> Poly;

#define EPS 1e-8
#define EQ (abs((a)-(b))<EPS)
const double PI = acos(-1.0);

inline int signum(double x) { return abs(x) < EPS ? 0 : x > 0 ? 1 : -1; }

namespace std {
    bool operator<(const P &a, const P &b) {
        return a.real() == b.real() ? a.imag() < b.imag() : a.real() < b.real();
    }
}

double dot(P a, P b) {
    return a.real() * b.real() + a.imag() * b.imag();
}

double cross(P a, P b) {
    return a.real() * b.imag() - a.imag() * b.real();
}

int ccw(P a, P b, P c) {
    b -= a;
    c -= a;
    int sign = signum(cross(b, c));
    if (abs(sign) == 1) return sign;
    if (signum(dot(b, c)) < 0) return -2;
    if (abs(b) < abs(c)) return 2;
    return 0;
}

double area_poly(Poly poly) {
    double ans = 0.0;
    for (int i = 0; i < poly.size(); i++) ans += cross(poly[i], poly[(i + 1) % poly.size()]);
    return abs(ans) / 2.0;
}

bool is_convex(Poly poly) {
    if (poly.size() < 3) return false;
    int s = -3;
    int n = poly.size();
    for (int i = 0; i < poly.size(); i++) {
        int r = ccw(poly[i], poly[(i + 1) % n], poly[(i + 2) % n]);
        if (s == -3 && abs(r) == 1) s = r;
        if (s * r == -1) return false;
    }
    return true;
}

Poly convex_hull(Poly poly) {
    Poly g;
    int n = poly.size();
    int m;

    if (n < 3) return poly;

    sort(poly.begin(), poly.end());
    for (int i = 0; i < poly.size(); i++) {
        while ((m = g.size()) >= 2 && ccw(g[m - 2], g[m - 1], poly[i]) <= 0) g.pop_back();
        g.push_back(poly[i]);
    }
    int t = g.size();
    for (int i = n - 2; i >= 0; i--) {
        while ((m = g.size()) > t && ccw(g[m - 2], g[m - 1], poly[i]) <= 0) g.pop_back();
        g.push_back(poly[i]);
    }
    g.pop_back();
    return g;
}

int p_in_poly(Poly poly, P p) {
    bool in = false;
    int n = poly.size();
    for (int i = 0; i < n; i++) {
        P a = poly[i] - p;
        P b = poly[(i + 1) % n] - p;
        if (a.imag() > b.imag()) swap(a, b);
        if (a.imag() <= EPS && EPS < b.imag() && cross(a, b) < -EPS) in = !in;
        if (cross(a, b) == 0 && dot(a, b) <= 0) return 1;
    }
    return in ? 2 : 0;
}

