        cin >> x >> y;
        poly[i] = P(x, y);
    }

    int Q;
    cin >> Q;

    while (Q--) {
        double x, y;
        cin >> x >> y;

        cout << p_in_poly(poly, P(x, y)) << endl;
    }

}

