#define EPS 1e-10

class Point {
public:
    double x, y;

    Point (double x = 0, double y = 0) : x(x), y(y) {}

    Point operator-(Point p) {
        return Point(x - p.x, y - p.y);
    }

    double dot(Point p) {
        return x * p.x + y * p.y;
    }

    double cross(Point p) {
        return x * p.y - y * p.x;
    }

    int contains(vector<Point> g) {
        int n = g.size();
        int cnt = 0;
        for (int i = 0; i < n; i++) {
            Point a = g[i] - *this, b = g[(i + 1) % n] - *this;
            if (abs(a.cross(b)) < EPS && a.dot(b) < EPS) {
                return 1;
            }
            if (a.y > b.y) {
                swap(a, b);
            }
            if (a.y < EPS && EPS < b.y && a.cross(b) > EPS) {
                cnt++;
            }
        }
        return (cnt % 2 ? 2 : 0);
    }
};

int main() {
