    cin >> n;
    vector<Point> polygon(n);
    for (int i = 0; i < n; i++) {
        cin >> polygon[i].x >> polygon[i].y;
    }
    cin >> q;
    Point p;
    while (q--) {
        cin >> p.x >> p.y;
        cout << p.contains(polygon) << endl;
    }

    return 0;
}

