bool eq(double a, double b) //a == b
{
    return (fabs(a - b) <= EPS);
}

bool neq(double a, double b) //a != b
{
    return (!eq(a, b));
}

bool lt(double a, double b) //a < b
{
    return (a - b < -EPS);
}

bool leq(double a, double b) //a <= b
{
    return (lt(a, b) || eq(a, b));
}

