	scanf("%d",&q);
	for(int i=0;i<q;i++){
		double gx,gy,R=0;
		scanf("%lf %lf",&gx,&gy);
		bool isPeriphery=false;
		for(int j=0;j<n;j++){
			int p1=j;
			int p2=(j+1)%n;
			double dx1,dy1,dx2,dy2,len1,len2;
			dx1=xs[p1]-gx;
			dy1=ys[p1]-gy;
			dx2=xs[p2]-gx;
			dy2=ys[p2]-gy;
			len1=hypot(dx1,dy1);
			len2=hypot(dx2,dy2);
			double gaiseki=dx1*dy2-dx2*dy1;
			double naiseki=dx1*dx2+dy1*dy2;

			if(naiseki<=0&&gaiseki==0){
				isPeriphery=true;
			}else{
				double aSinVal,sinVal;

			if(len1*len2>0){
					sinVal=gaiseki/(len1*len2);
				}else{
					isPeriphery=true;
					break;
				}
				if(sinVal<-1)sinVal=-1;
				if(sinVal>1)sinVal=1;
 				aSinVal=asin(sinVal);
				if(gaiseki>0){
					if(naiseki>=0)R+=aSinVal;
					else R+=M_PI-aSinVal;
				}else{
					if(naiseki>=0)R+=aSinVal;
					else R+=-aSinVal-M_PI;
				}
			}
 		}

		int type;
		if(fabs(R-M_PI*2)<0.0001){
			type=2;
		}else{
			type=0;
		}

		if(isPeriphery==true){
			type=1;
		}
		rs[i]=R;
		ans[i]=type;
	}
 	for(int i=0;i<q;i++){
		printf("%d\n",ans[i]);
	}
}

