int s_check(xy_t a, xy_t b, xy_t x, xy_t y) {
	long long s, t;
	long long x1 = a.real(), y1 = a.imag(),
		x2 = b.real(), y2 = b.imag();
	s = (y2 - y1)*x.real() - (x2 - x1)*x.imag() - x1*y2 + x2*y1;
	t = (y2 - y1)*y.real() - (x2 - x1)*y.imag() - x1*y2 + x2*y1;
	if ((s > 0 && t > 0) || (s < 0 && t < 0)) return 0;
	if ((s > 0 && t < 0) || (s < 0 && t > 0)) return 2;
	return 1;
}
