    Point operator/(double k) const { return Point(x / k, y / k); }

    bool operator<(const Point& p) const { return x != p.x ? x < p.x : y < p.y; }
    bool operator==(const Point& p) const { return fabs(x - p.x) < 1e-10 && fabs(y - p.y) < 1e-10; }

    static double norm(const Point& p) { return sqrt(p.x * p.x + p.y * p.y); }
    static double dot(const Point& p1, const Point& p2) { return p1.x * p2.x + p1.y * p2.y; }
    static double cross(const Point& p1, const Point& p2) { return p1.x * p2.y - p1.y * p2.x; }
};
struct Segment {
    Point s, t;
    Segment(const Point& s, const Point& t) : s(s), t(t) {}
    Segment(const Segment& seg) : s(seg.s), t(seg.t) {}
};

typedef vector<Point> Polygon;

int ccw(const Point& p1, const Point& p2, const Point& p) {
    double cr = Point::cross(p2 - p1, p - p1);
    if (cr > 0) return 1;
    if (cr < 0) return -1;
    // p, p1, p2.
    if (Point::dot(p2 - p1, p - p1) < 0) { return 2; }
    // p1, p2, p.
    if (Point::dot(p - p2, p1 - p2) < 0) { return -2; }
    // p is in [p1, p2].
    return 0;
}

bool intersect(const Segment& s1, const Segment& s2) {
    bool ok1 = ccw(s1.s, s1.t, s2.s) * ccw(s1.s, s1.t, s2.t) <= 0;
    bool ok2 = ccw(s2.s, s2.t, s1.s) * ccw(s2.s, s2.t, s1.t) <= 0;
    return ok1 && ok2;
}

int contains(Polygon g, Point p) {
    int n = g.size();
    bool x = false;
    for (int i = 0; i < n; i++) {
        Point a = g[i] - p, b = g[(i + 1) % n] - p;
        if (ccw(g[i], g[(i + 1) % n], p) < EPS) { return 1; }
        if (a.y > b.y) swap(a, b);
        if (a.y < EPS && EPS < b.y && Point::cross(a, b) > EPS) x = !x;
    }
    return (x ? 2 : 0);
}

int check_containment(const Polygon& poly, const Point p) {
    int n = poly.size();
    bool ans = false;

    rep(i, 0, n) {
        Point s(poly[i]), t(poly[(i + 1) % n]);
        if (ccw(s, t, p) == 0) { return 1; }
        if (s.y > t.y) { swap(s, t); }
        if (s.y < p.y + EPS && p.y + EPS < t.y && Point::cross(s - p, t - p) > EPS) { ans = !ans; }
    }
    return ans ? 2 : 0;
}

int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);

    int n;
    cin >> n;

    Polygon poly(n);
    rep(i, 0, n) { cin >> poly[i].x >> poly[i].y; }

    int q;
    cin >> q;
    rep(i, 0, q) {
        Point p;
        cin >> p.x >> p.y;
        cout << check_containment(poly, p) << endl;
    }

    return 0;
}

