}

int contains(Polygon g, Point p)
{
    int sz=g.v.size();
    bool flag=false;
    for(int i=0; i<sz; i++)
    {
        Vector a=g.v[i]-p;
        Vector b=g.v[(i+1)%sz]-p;
        if(fabs(Cross(a,b))==0 && Dot(a,b)<EPS)
            return 1;   //点在多?形?上
        if(a.y>b.y)
            swap(a,b);
        if(a.y<EPS && b.y>EPS && Cross(a,b)>EPS)
            flag=!flag;
    }
    return flag?2:0;   //点在多?形内/点在多?形外
}

