enum{OUT,ON,IN};
int contains(vector<P> poly,P p) {
  int n=poly.size();
  bool in=false;
  for(int i=0;i<n;i++){
    P a=poly[i]-p, b=poly[(i+1)%n]-p;
    if(a.Y>b.Y)swap(a,b);
    if(a.Y<=0&&0<b.Y){
      if(cross(a,b)<0)in=!in;
    }
    if(cross(a,b)==0&&dot(a,b)<=0)return ON;
  }
  return in?IN:OUT;
}

