        case -1:
        case -4:
        case 0:
            break;
        default:
            return false;
    }
    switch(ccw(p2,p3,p0)*ccw(p2,p3,p1)){
        case -1:
        case -4:
        case 0:
            break;
        default:
            return false;
    }
   return true;
}

bool isInside(polygon& g,vec& p){
