#define EPS 1e-6	

struct pt {
	int x, y;
	pt(){};
	pt(int _x, int _y) : x(_x), y(_y){};


	bool operator<(pt p) {
		return(x < p.x || (x == p.x && y < p.y));
	}

	bool operator==(pt p) const {
		return(x == p.x && y == p.y);
	};
};

pt points[100];


typedef std::pair<pt, pt> seg;

inline pt operator-(pt a, pt b) {
	return pt(a.x-b.x, a.y-b.y);
}

inline double cross(pt a, pt b) {
	return(a.x*b.y - a.y*b.x);
}

inline bool ccw(pt a, pt b, pt c) {
	return cross(b-a, c-a) >= 0;
}

inline double dot(pt a, pt b) {
	return (a.x*b.x + a.y*b.y);
}


void find(pt p, int numPoints) {
	double r;
	pt pa, pb;
	bool cut = false;

	// assume that there is a horizontal line ray with 
	// p as its origin in the easterly direction
	for (int i = 0; i < numPoints; ++i) {
		if (p == points[i]) {
			printf("1\n");
			return;
		}

		pa = points[i] - p;
		pb = points[(i+1)%numPoints] - p;

		// check that vectors are parallel && 
		// have 0 distance between the two
		// i.e. are collinear
		// printf("%lf %lf\n", cross(pa, pb), dot(pa, pb));
		if (cross(pa, pb) == 0 && dot(pa, pb) <= EPS) {
			printf("1\n");
			return;
		}

		// otherwise check if p-ray cuts across ab
		if (pa.y > pb.y) {
			pt temp = pa;
			pa = pb;
			pb = temp;
		}

		// pa points more up
		// pb points more down
		// i.e. ab is a vaguely vertical interval
		if (pa.y <= 0 && pb.y > 0) {
			// p-ray cuts between a and b
			if (cross(pa,pb) > 0) cut = (!cut);
		}


	}
	if (cut) {
		printf("2\n");
	} else {
		printf("0\n");
	}
}

int main() {
	int numPoints, numTests, val, a, b;
	scanf("%d", &numPoints);
	for (int i = 0; i < numPoints; ++i) {
		scanf("%d %d", &a, &b);
		points[i] = pt(a, b);
	}

	scanf("%d", &numTests);
	for (int i = 0; i < numTests; ++i) {
		scanf("%d %d", &a, &b);
		find(pt(a,b), numPoints);
	}

	return 0;
}

