const double EPS = 1e-8;

typedef pair<double, double> pt;
#define x first
#define y second

pt operator-(pt a, pt b) {
  return pt(a.x - b.x, a.y - b.y);
}

bool zero(double x) {
  return fabs(x) <= EPS;
}

double cross(pt a, pt b) {
  return a.x*b.y - a.y*b.x;
}

bool ccw(pt a, pt b, pt c) {
  return cross(b - a, c - a) >= 0;
}

/*
 * segment segment intersection
 */

typedef pair<pt, pt> seg;
#define a first
#define b second

bool collinear(seg a, seg b) { // all four points collinear
    // cout << a.a.x << " " << a.a.y << endl;
    // cout << a.b.x << " " << a.b.y << endl;

    // cout << b.a.x << " " << b.a.y << endl;
    // cout << b.b.x << " " << a.b.y << endl;

    return zero(cross(a.b - a.a, b.a - a.a)) &&
           zero(cross(a.b - a.a, b.b - a.a));
}

double sq(double a) { return a * a; }

double dist(pt p, pt q) {
  return sqrt(sq(p.x - q.x) + sq(p.y - q.y));
}

int intersect(seg a, seg b) {
    if (ccw(a.a, a.b, b.a) != ccw(a.a, a.b, b.b) &&
        ccw(b.a, b.b, a.a) != ccw(b.a, b.b, a.b)) return 1;

    return 0;
 }


