        printf("%2.f %.2f %.2f\n",p.x,p.y,p,r);
    }
    bool operator == (circle v){
        return (p==v.p)&&sign(r-v.r)==0;
    }
    bool operator < (circle v)const{
        return ((p<v.p)||((p==v.p)&&sign(r-v.r)<0));
    }
    db area(){return PI*r*r;}
    db circumference(){return 2*PI*r;}
    //|0 not|1 on|2 in|
    int relation(Point b){
        db dst=b.distance(p);
        if(sign(dst-r)<0)return 2;
        else if(sign(dst-r)==0)return 1;
        return 0;
    }
    //|0 not|1 on|2 in|
    int relationseg(Line v){
        db dst=v.dispointtoseg(p);
        if(sign(dst-r)<0)return 2;
        else if(sign(dst-r)==0)return 1;
        return 0;
    }
    int relationline(Line v){
        db dst=v.dispointtoline(p);
        if(sign(dst-r)<0)return 2;
        else if(sign(dst-r)==0)return 1;
        return 0;
    }
    //|1 内含|2 内切|3 相交|4 外切|5 相离|
    int relationcircle(circle v){
        db d=p.distance(v.p);
        if(sign(d-r-v.r)>0)return 5;
        if(sign(d-r-v.r)==0)return 4;
        db l=fabs(r-v.r);
        if(sign(d-r-v.r)<0&&sign(d-l)>0)return 3;
        if(sign(d-l)==0)return 2;
        if(sign(d-l)<0)return 1;
    }
    //|0 没有交点|1 一个交点|2 两个交点|
    int pointcrosscircle(circle v,Point &p1,Point &p2){
        int rel=relationcircle(v);
        if(rel==1||rel==5)return 0;
        db d=p.distance(v.p);
        db l=(d*d+r*r-v.r*v.r)/(2*d);
        db h=sqrt(r*r-l*l);
        Point tmp=p+(v.p-p).trunc(l);
        p1 = tmp+((v.p-p).rotleft().trunc(h));
		p2 = tmp+((v.p-p).rotright().trunc(h));
        if(rel==2||rel==4)return 1;
        return 2;
    }
    int pointcrossline(Line v,Point &p1,Point &p2){
        if(!(*this).relationline(v))return 0;
        Point a=v.lineprog(p);
        db d=v.dispointtoline(p);
        d=sqrt(r*r-d*d);
        if(sign(d)==0){
            p1=a;p2=a;
            return 1;
        }
        p1=a+(v.e-v.s).trunc(d);
        p2=a-(v.e-v.s).trunc(d);
        return 2;
    }
    int getcircle(Point a,Point b,db r1,circle &c1,circle &c2){
        circle x(a,r1),y(b,r1);
        int t=x.pointcrosscircle(y,c1.p,c2.p);
        if(!t)return 0;
        c1.r=c2.r=r;
        return t;
    }
    int getcircle(Line u,Point q,db r1,circle &c1,circle &c2){
        db dis=u.dispointtoline(q);
        if(sign(dis-r1*2)>0)return 0;
        if(sign(dis)==0){
            c1.p=q+((u.e-u.s).rotleft().trunc(r1));
            c2.p=q+((u.e-u.s).rotright().trunc(r1));
            c1.r=c2.r=r1;
            return 2;
        }
        Line u1=Line((u.s+(u.e-u.s).rotleft().trunc(r1)),(u.e+(u.e-u.s).rotleft().trunc(r1)));
		Line u2=Line((u.s+(u.e-u.s).rotright().trunc(r1)),(u.e+(u.e-u.s).rotright().trunc(r1)));
		circle cc = circle(q,r1);
		Point p1,p2;
		if(!cc.pointcrossline(u1,p1,p2))cc.pointcrossline(u2,p1,p2);
		c1 = circle(p1,r1);
		if(p1 == p2){
			c2 = c1;
			return 1;
		}
		c2 = circle(p2,r1);
		return 2;
    }
    int getcircle(Line u,Line v,db r1,circle &c1,circle &c2,circle &c3,circle &c4){
        if(u.parallel(v))return 0;//两直线平行
		Line u1=Line(u.s+(u.e-u.s).rotleft().trunc(r1),u.e+(u.e-u.s).rotleft().trunc(r1));
		Line u2=Line(u.s+(u.e-u.s).rotright().trunc(r1),u.e+(u.e-u.s).rotright().trunc(r1));
		Line v1=Line(v.s+(v.e-v.s).rotleft().trunc(r1),v.e+(v.e-v.s).rotleft().trunc(r1));
		Line v2=Line(v.s+(v.e-v.s).rotright().trunc(r1),v.e+(v.e-v.s).rotright().trunc(r1));
		c1.r=c2.r=c3.r=c4.r=r1;
		c1.p=u1.crosspoint(v1);
		c2.p=u1.crosspoint(v2);
		c3.p=u2.crosspoint(v1);
		c4.p=u2.crosspoint(v2);
		return 4;
    }
    int getcircle(circle cx,circle cy,double r1,circle &c1,circle &c2){
		circle x(cx.p,r1+cx.r),y(cy.p,r1+cy.r);
		int t=x.pointcrosscircle(y,c1.p,c2.p);
		if(!t)return 0;
		c1.r=c2.r=r1;
		return t;
	}
	int tangentline(Point q,Line &u,Line &v){
		int x   = relation(q);
		if(x == 2)return 0;
		if(x == 1){
			u = Line(q,q + (q-p).rotleft());
			v = u;
			return 1;
		}
		double d = p.distance(q);
		double l = r*r/d;
		double h = sqrt(r*r-l*l);
		u = Line(q,p + ((q-p).trunc(l) + (q-p).rotleft().trunc(h)));
		v = Line(q,p + ((q-p).trunc(l) + (q-p).rotright().trunc(h)));
		return 2;
	}
	double areacircle(circle v){
		int rel = relationcircle(v);
		if(rel >= 4)return 0.0;
		if(rel <= 2)return min(area(),v.area());
		double d = p.distance(v.p);
		double hf = (r+v.r+d)/2.0;
		double ss = 2*sqrt(hf*(hf-r)*(hf-v.r)*(hf-d));
		double a1 = acos((r*r+d*d-v.r*v.r)/(2.0*r*d));
		a1 = a1*r*r;
		double a2 = acos((v.r*v.r+d*d-r*r)/(2.0*v.r*d));
		a2 = a2*v.r*v.r;
		return a1+a2-ss;
	}
	double areatriangle(Point a,Point b){
		if(sign((p-a)^(p-b)) == 0)return 0.0;
		Point q[5];
		int len = 0;
		q[len++] = a;
		Line l(a,b);
		Point p1,p2;
		if(pointcrossline(l,q[1],q[2])==2){
			if(sign((a-q[1])*(b-q[1]))<0)q[len++] = q[1];
			if(sign((a-q[2])*(b-q[2]))<0)q[len++] = q[2];
		}
		q[len++] = b;
		if(len == 4 && sign((q[0]-q[1])*(q[2]-q[1]))>0)swap(q[1],q[2]);
		double res = 0;
		for(int i = 0;i < len-1;i++){
			if(relation(q[i])==0||relation(q[i+1])==0){
				double arg = p.rad(q[i],q[i+1]);
				res += r*r*arg/2.0;
			}
			else{
				res += fabs((q[i]-p)^(q[i+1]-p))/2.0;
			}
		}
		return res;
	}
};
int getTangents(circle A,circle B,Point *a,Point *b)
{
    int cnt=0;        //存切点用
    if(sign(A.r-B.r)<0){
        swap(A,B);
        swap(a,b);
    }
    double d=sqrt((A.p.x-B.p.x)*(A.p.x-B.p.x)+(A.p.y-B.p.y)*(A.p.y-B.p.y));     //圆心距
    double rdiff=A.r-B.r;      //两圆半径差
    double rsum=A.r+B.r;       //两圆半径和
    if(sign(d-rdiff)<0)return 0;        //1.内含
    double base=atan2(B.p.y-A.p.y,B.p.x-A.p.x);      //向量AB的极角
    if(sign(d)== 0)return -1;        //2.重合
    if(sign(d-rdiff) == 0){       //3.内切
        a[cnt]=b[cnt]=A.point(base);
        cnt++;
        return 1;
    }
    double ang=acos((A.r-B.r)/d);
    a[cnt]=A.point(base+ang);b[cnt]=B.point(base+ang);cnt++;      //4.相交（外切、外离的外公切线也在此求出）
    a[cnt]=A.point(base-ang);b[cnt]=B.point(base-ang);cnt++;      //两条外公切线的切点
    if(sign(d-rsum)==0){        //5.外切
        a[cnt]=b[cnt]=A.point(base);
        cnt++;
    }
    else if(sign(d-rsum)>0){      //6.外离
        double ang=acos((A.r+B.r)/d);
        a[cnt]=A.point(base+ang);b[cnt]=B.point(PI+base+ang);cnt++;
        a[cnt]=A.point(base-ang);b[cnt]=B.point(PI+base-ang);cnt++;
    }
    return cnt;
}
