	//`注意如果有影响，要特判下所有点共点，或者共线的特殊情况`
	void getconvex(polygon &convex){
		sort(p,p+n);
		convex.n=n;
		rep(i,0,min(n,2)-1)convex.p[i]=p[i];
		if(convex.n==2&&(convex.p[0]==convex.p[1]))convex.n--;//特判
		if(n<=2)return;
		int &top=convex.n;
		top=1;
		rep(i,2,n-1){
            while(top&&sign((convex.p[top]-p[i])^(convex.p[top-1]-p[i]))<=0)top--;
            convex.p[++top] = p[i];
		}
		int temp=top;
		convex.p[++top]=p[n-2];
		per(i,n-3,0){
            while(top!=temp&&sign((convex.p[top]-p[i])^(convex.p[top-1]-p[i]))<=0)top--;
			convex.p[++top]=p[i];
		}
		if(convex.n==2&&(convex.p[0]==convex.p[1]))convex.n--;//特判
		convex.norm();//`原来得到的是顺时针的点，排序后逆时针`
	}
	//`得到凸包的另外一种方法`
	void Graham(polygon &convex){
		norm();
		int &top=convex.n;
		top=0;
		if(n==1){
			top=1;
			convex.p[0]=p[0];
			return;
		}
		if(n==2){
			top=2;
			convex.p[0]=p[0];
			convex.p[1]=p[1];
			if(convex.p[0]==convex.p[1])top--;
			return;
		}
		convex.p[0]=p[0];
		convex.p[1]=p[1];
		top=2;
		rep(i,2,n-1){
            while(top>1&&sign((convex.p[top-1]-convex.p[top-2])^(p[i]-convex.p[top-2]))<=0)top--;
			convex.p[top++]=p[i];
		}
		if(convex.n==2&&(convex.p[0]==convex.p[1]))convex.n--;//特判
	}
	//`判断是不是凸的`
	bool isconvex(){
		bool s[2];
		memset(s,false,sizeof(s));
		rep(i,0,n-1){
            int j=(i+1)%n;
			int k=(j+1)%n;
			s[sign((p[j]-p[i])^(p[k]-p[i]))+1]=true;
			if(s[0]&&s[2])return false;
		}
		return true;
	}
	//`判断点和任意多边形的关系`
	//` 3 点上`
	//` 2 边上`
	//` 1 内部`
	//` 0 外部`
	int relationpoint(Point q){
	    rep(i,0,n-1){
            if(p[i]==q)return 3;
	    }
		getline();
		rep(i,0,n-1){
            if(l[i].pointonseg(q))return 2;
		}
		int cnt=0;
		rep(i,0,n-1){
            int j=(i+1)%n;
			int k=sign((q-p[j])^(p[i]-p[j]));
			int u=sign(p[i].y-q.y);
			int v=sign(p[j].y-q.y);
			if(k>0&&u<0&&v>=0)cnt++;
			if(k<0&&v<0&&u>=0)cnt--;
		}
		return cnt!=0;
	}
};


