       return true;
    }
    return false;
}

pt operator-(pt p, pt q) {
  return {p.x - q.x, p.y - q.y, -1};
}
pt operator+(pt p, pt q) {
  return {p.x + q.x, p.y + q.y, -1};
}
double cross(pt a, pt b) {
  return a.x*b.y - a.y*b.x;
}
bool ccw(pt a, pt b, pt c) {
  return cross(b - a, c - a) >= 0;
}
bool straight(pt a, pt b, pt c){
    return cross(b - a, c - a) == 0;
}
bool operator<(pt a, pt b) {
    return a.x < b.x;
}
double sq(double a) { return a * a; }
double dist(pt p, pt q) {
  return sqrt(sq(p.x - q.x) + sq(p.y - q.y));
}

vector<pt> half_hull(vector<pt> pts) {
  vector<pt> res;
  for (int i = 0; i < pts.size(); i++) {
    // ccw means we have a left turn; we don't want that
    while (res.size() >= 2 &&
           ccw(pts[i], res[res.size()-1], res[res.size()-2])) {
      res.pop_back();
    }
    res.push_back(pts[i]);
  }
  return res;
}

vector<pt> convex_hull(vector<pt> pts) {
  sort(pts.begin(), pts.end());
  vector<pt> top = half_hull(pts);
  reverse(pts.begin(), pts.end());
  vector<pt> bottom = half_hull(pts);
  top.pop_back();
  bottom.pop_back();
  vector<pt> res(top.begin(), top.end());
  res.insert(res.end(), bottom.begin(), bottom.end());
  return res;
}

int prevIndex(int i, int prev, int size) {
    int k =  i - prev;
    if (k < 0) {
        k = size + k ;
    }
