bool zero(double x) {
  return fabs(x) <= EPS;
}
seg operator*(seg a, int b) {
    return make_pair(a.a, pt({(a.b.x - a.a.x) * b, (a.b.y - a.a.y) * b, -1}));
}
seg getRay(seg a) {
    pt oldb = a.b;
    pt newb = pt{(a.b.x - a.a.x) * 10000, (a.b.y-a.a.y)*10000, -1};
    seg ans = make_pair(oldb, newb);
    return ans;
}
bool collinear(seg a, seg b) { // all four points collinear
  return zero(cross(a.b - a.a, b.a - a.a)) &&
         zero(cross(a.b - a.a, b.b - a.a));
}
int intersect(seg a, seg b) {
  if (collinear(a, b)) {
    double maxDist = 0;
    maxDist = max(maxDist, dist(a.a, a.b));
    maxDist = max(maxDist, dist(a.a, b.a));
    maxDist = max(maxDist, dist(a.a, b.b));
    maxDist = max(maxDist, dist(a.b, b.a));
    maxDist = max(maxDist, dist(a.b, b.b));
    maxDist = max(maxDist, dist(b.a, b.b));
    return (maxDist < dist(a.a, a.b) + dist(b.a, b.b) + EPS) ? 2 : 0;
  }
  return (ccw(a.a, a.b, b.a) != ccw(a.a, a.b, b.b) &&
         ccw(b.a, b.b, a.a) != ccw(b.a, b.b, a.b)) ? 2 : 0;
}

bool pointInSegment(pt a, seg b) {
    if (!straight(b.a, a, b.b)) return false;
    if (a.x <= max(b.a.x, b.b.x) && a.x >= min(b.a.x, b.b.x) &&
        a.y <= max(b.a.y, b.b.y) && a.y >= min(b.a.y, b.b.y))
    {   
            return true;        
    }
    return false;
}

