}

#include<bits/stdc++.h>

using namespace std;

struct pt
{
  int x, y;

  pt operator - ( pt & p ) const 
  {
    return { x - p.x, y - p.y };
  }

};

long long dot( pt p, pt q )
{
  return p.x * q.x + p.y * q.y;
}

long long cross( pt p, pt q )
{
  return p.x * q.y - p.y * q.x;
}

long long orient( pt a, pt b, pt c )
{
  return cross( b - a, c - a );
}

enum {IN,OUT,ON};

int point_in_polygon( vector< pt > &pol, pt p )
{
  int wn = 0;
  for( int i = 0, n = pol.size(); i < n; ++ i )
  {
    long long c = orient( p, pol[i], pol[(i+1)%n] );
    if( c == 0 && dot( pol[i] - p, pol[(i+1)%n] - p ) <= 0 ) return ON; 
    if( c > 0 && pol[i].y <= p.y && pol[(i+1)%n].y > p.y ) ++ wn;
    if( c < 0 && pol[(i+1)%n].y <= p.y && pol[i].y > p.y ) --wn;
  }
  return wn ? IN : OUT;
}

int main()
{
  int n;
  scanf("%d", &n );

  vector< pt > pol( n );
  for( auto &p : pol )
    scanf("%d %d", &p.x, &p.y );

  int q;
  scanf("%d", &q );
  while( q-- )
  {
    pt p;
    scanf("%d %d", &p.x, &p.y );

    int r = point_in_polygon( pol, p );
    if( r == IN ) puts("2");
    if( r == ON ) puts("1");
    if( r == OUT ) puts("0");
  }
  return 0;
}

#include <iostream>
