		point z=y-x;
		return sqrt(z.x*z.x+z.y*z.y);
	}
	double IP(point p,point q){
		return p.x*q.x+p.y*q.y;
	}
	double CP(point p,point q){
		return p.x*q.y-q.x*p.y;
	}
	string Counter_Clockwise(pp a,point x){
		point A=a.second-a.first;
		point X=x-a.first;
		double ip=IP(A,X),cp=CP(A,X),Al=Length(O,A),Xl=Length(O,X);
		if(cp>eps) return "Counter_Clockwise";
		if(cp<-eps) return "Clockwise";
		if(ip<-eps) return "Online_Back";
		if(Xl<Al||fabs(Xl-Al)<eps) return "On_Segment";
		return "Online_Front";
	}
	double Heron(point A,point B,point C){
		double a=Length(B,C),b=Length(C,A),c=Length(A,B),s=(a+b+c)/2;
		return sqrt(s*(s-a)*(s-b)*(s-c));
	}
	double Area(VP p){
		double sum=0,cp;
		p.push_back(p[0]);
		for(VP::iterator i=p.begin()+1;i!=p.end();i++){
			cp=CP(*(i-1),*i);
			if(cp>0) sum+=Heron(O,*(i-1),*i);
			else if(cp<0) sum-=Heron(O,*(i-1),*i);
		}
		return sum;
	}
	string Containment_polyon_point(VP p,point x){
		string s;
		double cp;
		p.push_back(p[0]);
		p.push_back(p[1]);
		double sum,area=Area(p);
		for(VP::iterator i=p.begin()+1;i!=p.end()-1;i++){
			s=Counter_Clockwise(pp(*(i-1),*i),x);
			cp=CP(*i-*(i-1),*(i+1)-*(i-1));
			if(s[3]=='S') return "On_side";
			if(cp>0) sum+=Heron(x,*i,*(i+1));
			else sum-=Heron(x,*i,*(i+1));
		}
		if(fabs(sum-area)<eps) return "Containment";
		return "not_Containment";
	}
