    int ccw(const P& a,P b,P c){
        b -= a; c -= a;
        if (cross(b,c) > EPS)   return +1;       // counter clockwise
        if (cross(b,c) < -EPS)   return -1;       // clockwise
        if (dot(b,c) < 0)     return +2;       // c--a--b on line
        if (norm(b) < norm(c)) return -2;       // a--b--c on line
        return 0;
    }
