    int contains(const Poly& ps, const P& p) {
        bool in = false;int n=ps.size();
        REP(i,n){
            P a = ps[i] - p, b = ps[pmod(i+1,n)] - p;
            if (a.Y > b.Y) swap(a, b);
            if (a.Y <= 0 && 0 < b.Y)
            if (cross(a, b) < 0) in = !in;
                if (cross(a, b) == 0 && dot(a, b) <= 0) return ON;
        }
        return in ? IN : OUT;
    }
