class VectorOperation {//操作符不必继承
public:
	double dot(Point a, Point b)//点乘
	{
		return a.x * b.x + a.y * b.y;
	}
	double cross(Point a, Point b)//叉乘
	{
		return a.x * b.y - a.y * b.x;
	}
	double abs(Vector s)
	{
		return sqrt(s.norm());
	}
	double getDistance(Point a, Point b)
	{
		return abs(a - b);
	}
	int PositionPL(Point p0, Point p1, Point p2)
	{
#define COUNTER_CLOCKWISE -1
#define CLOCKWISE 1
#define ONLINE_BACK -2
#define ONLINE_FRONT 2
#define ON_SEGMENT 0
		Vector a = p1 - p0;Vector b = p2 - p0;
		double Cross = cross(a, b);
		if (Cross > 0.0)return COUNTER_CLOCKWISE;
		if (Cross < 0.0)return CLOCKWISE;
		if (dot(a, b) < 0.0)return ONLINE_BACK;
		if (a.norm() < b.norm())return ONLINE_FRONT;

		return ON_SEGMENT;
#undef COUNTER_CLOCKWISE 
#undef CLOCKWISE 
#undef ONLINE_BACK 
#undef ONLINE_FRONT 
#undef ON_SEGMENT 
	}
	double inclination(Vector s)
	{
		return atan2(s.y, s.x);//取值范围-pi~pi
	}
	Vector Polar(double a, double r) { return Vector(cos(a) * r, sin(a) * r); }
	Vector rotate(Point a, double rad)//逆时针旋转
	{
		return Vector(a.x * cos(rad) - a.y * sin(rad), a.x * sin(rad) + a.y * cos(rad));
	}
};

class LineOperation :public VectorOperation{
public:	
	bool isOrthogonal(Line s1, Line s2)
	{
		return equals(dot(s1.p2-s1.p1, s2.p2 - s2.p1), 0.0);//操作符重载的返回值的内存分配机制尚不可知，似乎返回值没有分配内存，dot的参数不能用引用，否则报错。
	}
	bool isParallel(Line s1, Line s2)
	{
		return equals(cross(s1.p2 - s1.p1, s2.p2 - s2.p1), 0.0);//外积判断平行，内积判断垂直。
	}
	bool intersectSS(Line s1, Line s2)
	{
		return (PositionPL(s1.p1, s1.p2, s2.p1) * PositionPL(s1.p1, s1.p2, s2.p2) <= 0 &&
			    PositionPL(s2.p1, s2.p2, s1.p1) * PositionPL(s2.p1, s2.p2, s1.p2) <= 0);//即对两条线段都有另一条线段的两个端点必须在当前线段的两侧
	}//可以证明如果一条线段的计算结果是负二，另一个线段的计算结果必定大于0
	Point projection(Line s, Point p)
	{
		Vector base = s.p1 - s.p2;
		double r = dot(p - s.p1, base) /base.norm();
		return s.p1 + base * r;
	}
	Point reflection(Line s, Point p)
	{
		return p + (projection(s, p)-p) * 2.0;
	}
	double getDistanceLP(Line s, Point p)//点与直线
	{
		return fabs(cross(s.p2 - s.p1, p - s.p1)) / abs(s.p2 - s.p1);
	}
	double getDistanceSP(Line s, Point p)//点与线段
	{
		if (dot(s.p2 - s.p1, p - s.p1) < 0.0)return abs(p - s.p1);//与点p1成钝角
		else if (dot(s.p2 - s.p1, p - s.p2) > 0.0)return abs(p - s.p2);
		else return getDistanceLP(s, p);
	}
	double getDistanceSS(Line s1, Line s2)//线段与线段
	{
		//考虑
		// --
		//----
		if (intersectSS(s1, s2))return 0.0;//如果枚举优化需要16次逻辑，采用分治法的效率和直接使用min函数一样
		else
			return min(min(getDistanceSP(s1, s2.p1), getDistanceSP(s1, s2.p2)), min(getDistanceSP(s2, s1.p1), getDistanceSP(s2, s1.p2)));
	}
	Point getCrossPointSS(Line s1, Line s2)
	{
		Vector base = s2.p2 - s2.p1;
		double d1 = fabs(cross(base, s1.p1 - s2.p1));//除以abs(base)这步因为在t中base被消去所以没有写，提升效率
		double d2 = fabs(cross(base, s1.p2 - s2.p1));
		double t = d1 / (d1 + d2);
		return s1.p1 + (s1.p2 - s1.p1) * t;
	}
	Point getCrossPointLL(Line s1, Line s2)
	{
		Vector a = s1.p2 - s1.p1;
		Vector b = s2.p2 - s2.p1;
		return (s1.p1 + a*(cross(b, s2.p1 - s1.p1)) / cross(b, a));
	}
};

class CircleOperation :public VectorOperation{
public:
	int intersectCL(Line s,Circle c)
	{
		LineOperation op;
		double distance = op.getDistanceLP(s, c.c);
		double comparation = distance - c.r;
		if (comparation < EPS)return 1;
		if (comparation < 0.0)return 2;
		if (comparation > 0.0)return 0;
	}
	int intersectCC(Circle c1, Circle c2)
	{
		double distanceR = c1.r + c2.r;
		double distancePR = getDistance(c1.c, c2.c);
		double comparation = distancePR - distanceR;
		if (comparation < EPS)return 1;
		if (comparation > 0.0)return 0;
		if (comparation < 0.0)return 2;
	}
	pair<Point,Point> getCrossPointCL(Circle c,Line s)
	{
		LineOperation op;
		Point CLprojection = op.projection(s, c.c);
		Vector CL = CLprojection - c.c;
		Vector e = (s.p2 - s.p1) / abs(s.p2 - s.p1);
		double base = sqrt(c.r * c.r - CL.norm());
		return make_pair(CLprojection + e * base, CLprojection - e * base);
		//可以处理仅有一个交点的情况base是0产生两个同解。sqrt要求必须是正数，使用前必须进行有解性判断。
	}
	pair<Point, Point> getCrossPointCC(Circle c1, Circle c2)
	{
		double CC = abs(c1.c - c2.c);
		double a = acos((c1.r * c1.r + CC * CC - c2.r * c2.r) / (2 * c1.r * CC));//余弦定理
		double t = inclination(c2.c - c1.c);
		return make_pair(c1.c + Polar(t + a, c1.r), c1.c + Polar(t - a, c1.r));
	}
};

class PolygonOperation :public VectorOperation{
public:
	int PointsContainment(Polygon &g,Point p)
	{//ON_SEGMENT 0 IN 1 OUT 2
#define ON_SEGMENT 0
#define IN 1
#define OUT 2
		int size = g.size();
		bool x = false;
		Vector a, b;
		forup(i, 0, size)
		{
			a = g[i] - p; b = g[(i+1)%size] - p;//用取模创造循环
			if (fabs(cross(a, b)) < EPS && dot(a, b) < EPS)return ON_SEGMENT;//<EPS包含了内积为负数的情况
			if (a.y < b.y)
			{
				double test = cross(a, b);
				if (a.y < EPS && EPS<b.y && cross(a, b)>EPS)x = !x;
			}
			else
			{
				if (b.y < EPS && EPS<a.y && cross(b, a)>EPS)x = !x;//射线平行x轴指向x轴正向,如果是.----平行的情况跳过，不会更改x值EPS<a.y不成立
			}//如果是-----.的情况，则会“触碰“到两条边，该判断方法仍然正确：触碰到偶数条边时点在多边形外部
		}
		return x ? IN : OUT;
#undef ON_SEGMENT
#undef IN
#undef OUT
	}
};
