using namespace std;

struct Point {
	double x, y;
	Point() : x(0), y(0) { }
	Point(double x, double y) : x(x), y(y) { }
	Point operator+(const Point &p) const { return Point(x + p.x, y + p.y); }
	Point operator-(const Point &p) const { return Point(x - p.x, y - p.y); }
	Point operator*(double s) const { return Point(x * s, y * s); }
	Point operator/(double s) const { return Point(x / s, y / s); }
	Point &operator+=(const Point &p){ x += p.x; y += p.y; return *this; }
	Point &operator-=(const Point &p){ x -= p.x; y -= p.y; return *this; }
	Point &operator*=(double s){ x *= s; y *= s; return *this; }
	Point &operator/=(double s){ x /= s; y /= s; return *this; }
	bool operator==(const Point &p) const { return x == p.x && y == p.y; }
	bool operator!=(const Point &p) const { return x != p.x || y != p.y; }
	bool operator<(const Point &p) const {
		return (x == p.x) ? (y < p.y) : (x < p.x);
	}
};
inline Point operator*(double s, const Point &p){ return p * s; }

inline double norm(const Point &p){ return p.x * p.x + p.y * p.y; }
inline double abs(const Point &p){ return sqrt(norm(p)); }
inline double arg(const Point &p){ return atan2(p.y, p.x); }
inline Point unit(const Point &p){ return p / abs(p); }
inline Point ortho(const Point &p){ return Point(-p.y, p.x); }
inline double cross(const Point &a, const Point &b){
	return a.x * b.y - a.y * b.x;
}
inline double dot(const Point &a, const Point &b){
	return a.x * b.x + a.y * b.y;
}

class Polygon {
private:
	vector<Point> m_points;
public:
	Polygon() : m_points() { }
	template <typename Iterator>
	Polygon(Iterator begin, Iterator end) : m_points(begin, end) { }
	const Point &operator[](int i) const {
		return m_points[i % m_points.size()];
	}
	Point &operator[](int i){ return m_points[i % m_points.size()]; }
	size_t size() const { return m_points.size(); }
};

inline double area(const Polygon &g){
	const int n = g.size();
	double sum = 0;
	for(int i = 0; i < n; ++i){ sum += cross(g[i], g[i + 1]); }
	return sum * 0.5;
}
inline int contains(const Polygon &g, const Point &p){
	const int n = g.size();
	int result = 0;
	for(int i = 0; i < n; ++i){
		Point a = g[i] - p, b = g[i + 1] - p;
		if(a.y > b.y){ swap(a, b); }
		if(a.y <= 0 && 0 < b.y && cross(a, b) < 0){ result = 1 - result; }
		if(cross(a, b) == 0 && dot(a, b) <= 0){ return -1; } // on line
	}
	return result;
}

