	for( int i = 0; i < n; i++ ) {
		cin >> g[i].x >> g[i].y;
	}

	int q;
	cin >> q;
	for( int i = 0; i < q; i++ ) {
		point p;
		cin >> p.x >> p.y;
		bool x = false;
		int ans = 0;
		for( int j = 0; j < n; j++ ) {
			point a = g[j] - p;
			point b = g[(j + 1) % n] - p;
			if( dot( a, b ) <= 0 && cross( a, b ) == 0 ) { ans = 1; break; }
			if( a.y > b.y ) swap( a, b );
			if( a.y <= 0 && b.y > 0 && cross( a, b ) > 0 ) x = !x;
		}
		if( ans == 0 && x ) ans = 2;
		cout << ans << endl;
	}
}

