#define _NEXT(i) (((i) + 1) == n ? 0 : ((i) + 1))

namespace asl
{
    template <typename numeric_type>
    using polygon = std::vector<point<numeric_type>>;

    enum PointInPolygon
    {
        OUT = 0,
        ON = 1,
        IN = 2,
    };

    template <typename numeric_type>
    PointInPolygon contains(const polygon<numeric_type> &P, const point<numeric_type> &p)
    {
        bool in = false;
        for (int i = 0, n = P.size(); i < n; ++i)
        {
            point<numeric_type> a = P[i] - p, b = P[_NEXT(i)] - p;
            if (imag(a) > imag(b))
                std::swap(a, b);

            if (imag(a) <= 0 && 0 < imag(b))
                if (cross(a, b) < 0)
                    in = !in;

            if (cross(a, b) == 0 && dot(a, b) <= 0)
                return PointInPolygon::ON;
        }
        return in ? PointInPolygon::IN : PointInPolygon::OUT;
    }

} 

